mapper.select:
select *
from (
	select n.*, row_number() over (order by notice_created_date desc) rn
	from notice n

		NOTICE_DELETED = 'N'



					and notice_title like '%' || #{value} || '%'


					and notice_content like '%' || #{value} || '%'




)
where rn between #{beginIndex} and #{endIndex}
